import React from 'react';

const EmergencyFloatingButton = () => {
  const handleEmergencyClick = () => {
    // In a real app, this would connect to the emergency system
    if (window.confirm('This will contact emergency services. Do you need immediate assistance?')) {
      window.location.href = 'tel:911';
    }
  };

  return (
    <button
      onClick={handleEmergencyClick}
      className="fixed bottom-6 right-6 bg-red-600 hover:bg-red-700 text-white rounded-full p-4 shadow-lg z-50 transition-colors duration-200"
      aria-label="Emergency Button"
    >
      <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
      </svg>
    </button>
  );
};

export default EmergencyFloatingButton;
